mapper.select:
select * from store where exposure_status = 1
and ( address like '%${keyword}%' or name like '%${keyword}%' )
order by store_no desc limit ${paging.startRow},${paging.endRow}

mapper.insert:
INSERT INTO store (
    name, address, detail_address, zonecode, phone, x, y, service
) VALUES (
    #{name}, #{address}, #{detailAddress}, #{zonecode}, #{phone}, #{x}, #{y}, #{service}
)
